<?php $titre = 'Contact';$description = 'Page contact de '.$rjson_site['titre'];$script = '<script src="dist/js/jquery.js" defer></script><script src="dist/js/bootstrap.min.js" defer></script>';$css = '';if(isset($_POST['nom']) && isset($_POST['mail'])){	$okform = 'non';	if(empty($_POST['nom']))	{		$err = '<div class="alert alert-danger" role="alert">La saisie du Nom est obligatoire</div>';	}	if(empty($_POST['mail']))	{		$err = '<div class="alert alert-danger" role="alert">La saisie d\'un mail est obligatoire</div>';	}	if(!empty($_POST['nom']) && !empty($_POST['mail']))	{		$nom = htmlspecialchars($_POST['nom']);		$mail = $_POST['mail'];		$dem = htmlspecialchars($_POST['dem']);				$observa = $_POST['observa'];		if($observa == 'NR')		{			$mails = $rjson_site['email'];					}		else		{			include CHEMIN_MODELE.'article.php';			$anim = animateur($observa);			if(count($anim) > 1)			{				foreach($anim as $n)				{					$tabm[] = $n['mail'];				}				$mails = implode(", ", $tabm);			}			else			{				$mails = $anim[0]['mail'];			}		}				$meshtml = '<html><head><meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" /></head>		<body>Contact de '.$rjson_site['titre'].'.<br /> 		Nom : '.$nom.', Adresse mail : '.$mail.'<br />'.$dem;		$mestxt = 'Contact de '.$rjson_site['titre'].'. Nom = '.$nom.', Adresse mail : '.$mail.' - '.$dem;		$frontiere = '-----=' . md5(uniqid(mt_rand()));				$headers ='From: '.$mail.''."\n"; 		$headers .= 'MIME-Version: 1.0'."\n";		$headers .= 'Content-Type: multipart/alternative; boundary="'.$frontiere.'"'."\n";		$messagem = 'This is a multi-part message in MIME format.'."\n\n";		$messagem .= '--'.$frontiere."\n";		$messagem .= 'Content-Type: text/plain; charset=UTF-8'."\n";		$messagem .= 'Content-Transfer-Encoding: 8bit'."\n\n";		$messagem .= $mestxt."\n\n";		$messagem .= '--'.$frontiere."\n";		$messagem .= 'Content-Type: text/html; charset=UTF-8'."\n";		$messagem .= 'Content-Transfer-Encoding: 8bit'."\n\n";		$messagem .= $meshtml."\n\n";		$messagem .= '--'.$frontiere.'--'."\n";			$ok = (mail($mails, $rjson_site['titre'].' Contact', $messagem, $headers)) ? 'oui' : 'non';			}}else{	$choix = (isset($_GET['d'])) ? $_GET['d'] : '';	$okform = 'oui';}include CHEMIN_VUE.'contact.php';?>