<?php$titre = 'Taxref - Mise à jour';$description = 'Liste des changements dans taxref sur le site'; $script = '<script src="dist/js/jquery.js" defer></script><script src="dist/js/bootstrap.min.js" defer></script>';$css = '';$json_t = file_get_contents('json/taxref.json');$rtaxref = json_decode($json_t, true);if($rtaxref['maj'] == 'oui'){	include CHEMIN_MODELE.'taxref.php';		$titre = 'Taxref - Mise à jour V'.$rtaxref['version'];	$description = 'Liste des changements dans taxref sur le site - version '.$rtaxref['version'];		$versionp = $rtaxref['version'] - 1;		$liste = changement();	foreach($liste as $n)	{		if($n['ancrang'] == 'ES') { $ancrang = 'espèce'; }		elseif($n['ancrang'] == 'SSES') { $ancrang = 'sous espèce'; }		elseif($n['ancrang'] == 'GN') { $ancrang = 'genre'; }		if($n['rang'] == 'ES') { $rang = 'espèce'; }		elseif($n['rang'] == 'SSES') { $rang = 'sous espèce'; }		elseif($n['rang'] == 'GN') { $rang = 'genre'; }		if($n['rang'] == 'GN')		{			$lien = 'observatoire/index.php?module=fiche&amp;action=ficheg&amp;d='.$n['observatoire'].'&amp;id='.$n['valfinal'];		}		else		{			$lien = 'observatoire/index.php?module=fiche&amp;action=fiche&amp;d='.$n['observatoire'].'&amp;id='.$n['valfinal'];		}		$tab[] = ['lien'=>$lien,'ancrang'=>$ancrang,'rang'=>$rang,'observatoire'=>$n['observatoire'],'ancnom'=>$n['ancnom'],'ancvern'=>$n['ancvern'],'nom'=>$n['nom'],'nomvern'=>$n['nomvern']];			}	}include CHEMIN_VUE.'taxref.php';