<?php$titre = '';$description = '';$script = '<script src="../dist/js/jquery.js" defer></script><script src="../dist/js/bootstrap.min.js" defer></script>';$css = '';if(isset($_GET['id'])){	include CHEMIN_MODELE.'famille.php';		$cdnom = htmlspecialchars($_GET['id']);		$fam = recherche_famille($cdnom,$nomvar);		$titre = 'Liste des '.$fam['famille'];	$description = 'Liste des espèces de la famille des '.$fam['famille'].' '.$rjson_site['ad1'].' '.$rjson_site['lieu'];	$latin = (isset($_SESSION['latin'])) ? $_SESSION['latin'] : '';	if($latin == 'oui') { $afflatin = 'oui'; }	elseif($rjson_obser['latin'] == 'oui' && ($latin == 'defaut' || $latin == '')) { $afflatin = 'oui'; }	elseif($latin == 'non') { $afflatin = 'non'; }	elseif($rjson_obser['latin'] == 'non' && ($latin == 'defaut' || $latin == '')) { $afflatin = 'non'; }		$listerang = rechercher_rang($nomvar);	foreach($listerang as $n)	{		if($n['idrang'] == 5) { $tstribu = 'oui'; }		if($n['idrang'] == 6) { $ttribu = 'oui'; }		if($n['idrang'] == 7) { $tsfam = 'oui'; }	}	$stribu = (isset($tstribu)) ? 'oui' : 'non';	$tribu = (isset($ttribu)) ? 'oui' : 'non';	$sfam = (isset($tsfam)) ? 'oui' : 'non';		if($sfam == 'oui')	{		$listesfam = recherche_sfstfam($nomvar,$stribu,$tribu,$cdnom);		foreach($listesfam as $n)		{			if(empty($n['cdnom'])) { $autre = 'oui'; }			if(!empty($n['cdnom'])) { $oksfam = 'oui'; }						}		if(isset($autre))		{			$autre = liste_sousfamille_autre($nomvar,$stribu,$tribu,$cdnom,$afflatin);			$nbautre = count($autre);		} else { $nbautre = 0; }		if(isset($oksfam))		{			$liste = liste_sousfamille($nomvar,$stribu,$tribu,$cdnom,$afflatin);			$nbliste = count($liste);			foreach($liste as $n)			{				$tabsbfm[$n['sbfm']] = ['sousfamille'=>$n['sousfamille'],'sbfm'=>$n['sbfm']];				if($afflatin == 'oui')				{					$libnom = '<a href="index.php?module=fiche&amp;action=fiche&amp;d='.$nomvar.'&amp;id='.$n['cdnom'].'"><i>'.$n['nom'].'</i> '.$n['auteur'].'</a>';				}				else				{					$libnom = (!empty($n['nomvern'])) ? '<a href="index.php?module=fiche&amp;action=fiche&amp;d='.$nomvar.'&amp;id='.$n['cdnom'].'">'.$n['nomvern'].' <i>('.$n['nom'].')</i> '.$n['auteur'].'</a>' : '<a href="index.php?module=fiche&amp;action=fiche&amp;d='.$nomvar.'&amp;id='.$n['cdnom'].'"><i>'.$n['nom'].'</i> '.$n['auteur'].'</a>';				}				$tabtaxon[] = ['sbfm'=>$n['sbfm'], 'nom'=>$libnom];			}		}  else { $nbliste = 0; }		$nbfam = $nbautre + $nbliste;	}		else	{		$autre = liste_sans_sousfamille($nomvar,$cdnom,$afflatin);		$nbfam = count($autre);	}	$nblibfam = ($nbfam > 1) ? 'Liste des '.$nbfam.' espèces :' : 'Une espèce :';			include CHEMIN_VUE.'famille.php';}